import { useForm } from "react-hook-form";
import React from "react";
import { Link } from "react-router-dom";

const Login = () => {
    const { register, handleSubmit, formState: { errors } } = useForm();
    const onSubmit = data => console.log(data);
    console.log(errors);

    return(
        <div className="login">
            <h1 style={{fontSize: 45, fontWeight:"lighter"}}>
                Privacy is important to us. Kind of.
            </h1>
            <h3 style={{fontSize: 20, fontWeight:"lighter"}}>
                Become an RTT Administrator today and gain access to all of the data collected by the forms hosted on our platform.
            </h3>
            <form onSubmit={handleSubmit(onSubmit)}>
                <input type="text" placeholder="Username" style={{ padding: '20px 5px', width:300, margin:"20px auto"}} {...register("Username", {})} />
                <br /><input type="password" placeholder="Password" style={{ padding: '20px 5px', width:300, margin:"20px auto"}} {...register("Password", {})} />

                <input type="submit" value="Sign Up" style={{ padding: '20px 5px', width:300, margin:"20px auto"}} />
            </form>
        </div>
        
    );
}

export default Login
