import { useForm } from "react-hook-form";
import React from "react";
import { Link } from "react-router-dom";

const Create = () => {
    const { register, handleSubmit, formState: { errors } } = useForm();
    const onSubmit = data => console.log(data);
    console.log(errors);

    return(
        <div className="create">
            <h1 style={{fontSize: 50, fontWeight: "lighter"}}>Webform Creator</h1>
            <h3 style={{fontWeight:"lighter", color: '#777'}}>Add or remove custom fields to complete your new webform.</h3>
            
            <form onSubmit={handleSubmit(onSubmit)}>
            <input type="text" placeholder="Field Name" style={{ padding: '20px 5px', width:300, margin:"20px auto"}} defaultValue={""} {...register("Field Name", {})} />
            <select {...register("Select Field")}>
                <option value="Text Field">Text Field</option>
                <option value=" Drop-Down Selection"> Drop-Down Selection</option>
                <option value=" Radio Selection"> Radio Selection</option>
                <option value=" Phone Number"> Phone Number</option>
                <option value=" Password"> Password</option>
                <option value=" Email"> Email</option>
            </select>
            <br /><input type="submit" value="Add Field" style={{ padding: '20px 5px', width:300, margin:"20px auto"}} />
    </form>
            
            
            <br /><Link to="/" style={{textDecoration: 'none', color: '#888'}}>
                Go Back
                </Link>
        </div>
        
    );
}

export default Create
